mapper.select:
SELECT



FROM scs_order



        AND reader_id = #{readerId}


        AND column_id = #{columnId}


        AND column_quote_id = #{columnQuoteId}


        AND payment_id = #{paymentId}


        AND status = #{status}







        ORDER BY create_time ASC


        ORDER BY create_time DESC




        ORDER BY update_time ASC


        ORDER BY update_time DESC
